import React, { Component } from 'react';
import PropTypes from 'prop-types';

class Tab extends Component {
  static propTypes = {
    activeTab: PropTypes.string.isRequired,
    dropdown:PropTypes.instanceOf(Array).isRequired,
    label: PropTypes.string.isRequired,
    onClick: PropTypes.func.isRequired,
    onClickDropdown: PropTypes.func.isRequired,
  };

  onClick = () => {
    const { label, onClick } = this.props;
    onClick(label);
  }

  onClickDropdown = () => {
    const { onClickDropdown } = this.props;
    onClickDropdown(0);
  }

  render() {
    const {
      onClick,
      onClickDropdown,
      props: {
        activeTab,
        dropdown,
        label,
      },
    } = this;

    let className = 'tab-list-item' + ' dropdown';  //make sure to insert a space

    if (activeTab === label) {
      className += ' tab-list-active';
    }

    return (
      <div
        className={className}
        onClick={onClick}
      >
        {label}
        <div className="dropdown-content">
          {
            dropdown.map((dropdownItem) => {
              return (<a onClick={() => this.onClickDropdown(dropdownItem)}>{dropdownItem}</a>);
            })
          }
        </div>
      </div>
    );
  }
}

export default Tab;
